    public static String Spin(String originalString, String site) {
        HashSet<Character> specialChar = new HashSet<Character>();
        specialChar.add('{');
        specialChar.add('|');
        int size = originalString.length();
        char[] originalChars = originalString.toCharArray();
        char[] stack = new char[size];
        int n = 0;
        int count = 0;
        int max = 0;
        for (int i = 0; i < originalChars.length; i++) {
            if (originalChars[i] == '{') {
                count++;
                if (count > max) {
                    max = count;
                }
            } else if (originalChars[i] == '}') {
                count--;
            }
        }


        int[] posOpen = new int[max];
        int currentOpen = 0;
        Random r = new Random();
        for (int i = 0; i < originalChars.length; i++) {
            if (n > 0) {
                if (originalChars[i] == '}') {
                    if (stack[n - 1] == '{') {
                        n--;
                        currentOpen--;
                        continue;
                    } else if (stack[n - 1] == '|') {
                        n--;
                    }
                } else if (originalChars[i] == '|') {
                    if (stack[n - 1] == '|') {
                        continue;
                    } else if (stack[n - 1] == '{') {
                        continue;
                    }
                }
            }
            if (originalChars[i] != '}') {
                stack[n] = originalChars[i];
                n++;
                if (originalChars[i] == '{') {
                    currentOpen++;
                    posOpen[currentOpen - 1] = n - 1;
                }
            } else {
                if (currentOpen == 0) {
                    stack[n] = originalChars[i];
                    n++;
                } else {
                    int slashCount = 0;
                    for (int j = posOpen[currentOpen - 1] + 1; j < n; j++) {
                        if (stack[j] == '|') {
                            slashCount++;
                        }
                    }
                    int choice = r.nextInt(slashCount + 1);
                    int tmpSlashCount = 0;
                    int pos = posOpen[currentOpen - 1];
                    while (tmpSlashCount < choice) {
                        pos = pos + 1;
                        if (stack[pos] == '|') {
                            tmpSlashCount++;
                        }
                    }
                    pos = pos + 1;
                    int tmp = posOpen[currentOpen - 1];
                    if (pos < n && stack[pos] != '|') {
                        while (pos < n) {
                            stack[tmp] = stack[pos];
                            tmp++;
                            pos++;
                            if (pos >= n || stack[pos] == '|' || stack[pos] == '}' || stack[pos] == '{') {
                                break;
                            }
                        }
                    }
                    n = tmp;
                    currentOpen--;
                }
            }
        }
        return new String(stack, 0, n);
    }

